import React, { useEffect, useRef, useState } from "react";
import maplibregl from "maplibre-gl";
import "maplibre-gl/dist/maplibre-gl.css";
import "./styles.css";

const OPENFREEMAP_STYLE = "https://tiles.openfreemap.org/styles/positron";

export default function App() {
  const mapContainerRef = useRef(null);
  const mapRef = useRef(null);
  const [lang, setLang] = useState("en");
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const base = process.env.REACT_APP_API_BASE || "http://localhost:4000";

  // âœ… Initialize MapLibre
  useEffect(() => {
    if (!mapContainerRef.current) return;

    const map = new maplibregl.Map({
      container: mapContainerRef.current,
      style: OPENFREEMAP_STYLE,
      center: [77.58, 12.94], // South Bangalore
      zoom: 12.5,
    });

    map.addControl(new maplibregl.NavigationControl(), "top-right");
    mapRef.current = map;

    map.on("load", () => {
      console.log("âœ… Map loaded successfully");
      setIsMapLoaded(true);
    });

    return () => map.remove();
  }, []);

  // âœ… Fetch + Render GeoJSON (with Kannada if selected)
  const loadGeoJSON = async (language = "en") => {
    try {
      console.log(`ðŸŒ Fetching data for: ${language}`);
      const response = await fetch(`${base}/api/places-postgis?lang=${language}`);

      if (!response.ok) {
        console.error("âŒ Backend error:", response.statusText);
        return;
      }

      const geojson = await response.json();
      console.log("âœ… Received GeoJSON features:", geojson.features?.length);

      const map = mapRef.current;
      if (!map) return;

      // âœ… Update if source already exists
      if (map.getSource("places")) {
        map.getSource("places").setData(geojson);
        return;
      }

      // âœ… Add GeoJSON source
      map.addSource("places", { type: "geojson", data: geojson });

      // âœ… Add circle markers
      map.addLayer({
        id: "places-circle",
        type: "circle",
        source: "places",
        paint: {
          "circle-radius": 6,
          "circle-color": "#007AFF",
          "circle-stroke-color": "#fff",
          "circle-stroke-width": 1.5,
        },
      });

      // âœ… Add text labels (Kannada or English)
      map.addLayer({
        id: "places-labels",
        type: "symbol",
        source: "places",
        layout: {
          "text-field": ["coalesce", ["get", "name_kn"], ["get", "name"]],
          "text-font": ["Noto Sans Kannada Regular", "Arial Unicode MS Regular"],
          "text-size": 13,
          "text-offset": [0, 1.2],
          "text-anchor": "top",
        },
        paint: {
          "text-color": "#222",
          "text-halo-color": "#fff",
          "text-halo-width": 1.2,
        },
      });

      // âœ… Popup on click
      map.on("click", "places-circle", (e) => {
        const props = e.features[0].properties;
        const name = props.name_kn || props.name;
        const category = props.category || "Unknown";
        new maplibregl.Popup()
          .setLngLat(e.lngLat)
          .setHTML(`<b>${name}</b><br><small>${category}</small>`)
          .addTo(map);
      });

      // âœ… Cursor hover style
      map.on("mouseenter", "places-circle", () => {
        map.getCanvas().style.cursor = "pointer";
      });
      map.on("mouseleave", "places-circle", () => {
        map.getCanvas().style.cursor = "";
      });
    } catch (err) {
      console.error("âŒ Failed to fetch GeoJSON:", err);
    }
  };

  // âœ… Load data once map is ready or lang changes
  useEffect(() => {
    if (isMapLoaded) loadGeoJSON(lang);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isMapLoaded, lang]);

  // âœ… Toggle language
  const handleTranslate = () => {
    const nextLang = lang === "en" ? "kn" : "en";
    console.log("ðŸ”„ Switching language to:", nextLang);
    setLang(nextLang);
  };

  return (
    <div className="app">
      <header className="topbar">
        <h1>Bhuvan Kannada Map (OpenFreeMap + MapLibre)</h1>
        <button className="btn" onClick={handleTranslate}>
          {lang === "en" ? "Translate â†’ Kannada" : "Show English"}
        </button>
      </header>

      <div ref={mapContainerRef} className="map-container" />

      <footer className="footer">
        Base Map: OpenFreeMap (Positron) | Data: PostGIS | Translation: Lingvanex
      </footer>
    </div>
  );
}
